/* Custom CSS Variables */
:root {
    --primary-orange: #FF6B35;
    --secondary-turquoise: #17A2B8;
    --accent-blue: #007BFF;
    --background-white: #FFFFFF;
    --text-dark: #343A40;
    --light-grey: #F8F9FA;
    --border-light: #E9ECEF;
    --success-green: #28A745;
    --spacing-base: 32px;
    --border-radius: 8px;
    --box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
    --transition: all 0.3s ease;
}

/* Global Styles */
* {
    box-sizing: border-box;
}

body {
    font-family: 'Inter', 'Roboto', sans-serif;
    background-color: var(--light-grey);
    color: var(--text-dark);
    line-height: 1.6;
    margin: 0;
    padding: 0;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
    font-weight: 600;
    line-height: 1.4;
    margin-bottom: 1rem;
}

.step-title {
    font-size: 2rem;
    font-weight: 700;
    color: var(--text-dark);
    margin-bottom: 0.5rem;
}

.step-subtitle {
    font-size: 1.1rem;
    color: #6C757D;
    margin-bottom: 0;
}

.section-title {
    font-size: 1.5rem;
    font-weight: 600;
    color: var(--text-dark);
}

.subsection-title {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--text-dark);
}

/* Container */
.wizard-container {
    min-height: 100vh;
    padding: var(--spacing-base) 0;
}

/* Progress Bar */
.progress-wrapper {
    margin-bottom: calc(var(--spacing-base) * 2);
}

.progress-bar-container {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: 600px;
    margin: 0 auto;
    padding: 0 var(--spacing-base);
}

.progress-step {
    display: flex;
    flex-direction: column;
    align-items: center;
    position: relative;
    z-index: 2;
}

.step-number {
    width: 48px;
    height: 48px;
    border-radius: 50%;
    background-color: var(--border-light);
    color: #6C757D;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    font-size: 1.1rem;
    transition: var(--transition);
    margin-bottom: 8px;
}

.step-label {
    font-size: 0.875rem;
    font-weight: 500;
    color: #6C757D;
    transition: var(--transition);
}

.progress-step.active .step-number {
    background-color: var(--accent-blue);
    color: white;
}

.progress-step.active .step-label {
    color: var(--accent-blue);
}

.progress-step.completed .step-number {
    background-color: var(--success-green);
    color: white;
}

.progress-step.completed .step-label {
    color: var(--success-green);
}

.progress-line {
    flex: 1;
    height: 2px;
    background-color: var(--border-light);
    margin: 0 16px;
    position: relative;
    top: -30px;
    z-index: 1;
}

/* Step Content */
.step-content {
    display: none;
    opacity: 0;
    transform: translateY(20px);
    transition: var(--transition);
}

.step-content.active {
    display: block;
    opacity: 1;
    transform: translateY(0);
}

.step-header {
    margin-bottom: calc(var(--spacing-base) * 1.5);
}

/* Theme Selection */
.theme-selection-section {
    background: white;
    border-radius: var(--border-radius);
    padding: var(--spacing-base);
    box-shadow: var(--box-shadow);
}

.theme-card {
    background: white;
    border: 2px solid var(--border-light);
    border-radius: var(--border-radius);
    padding: 20px;
    transition: var(--transition);
    cursor: pointer;
    height: 100%;
    position: relative;
}

.theme-card:hover {
    border-color: var(--accent-blue);
    box-shadow: 0 8px 24px rgba(0, 123, 255, 0.15);
}

.theme-card.selected {
    border-color: var(--success-green);
    box-shadow: 0 8px 24px rgba(40, 167, 69, 0.15);
}

.theme-preview {
    margin-bottom: 20px;
    border-radius: 6px;
    overflow: hidden;
    background: #f8f9fa;
    aspect-ratio: 4/3;
}

.theme-mockup {
    width: 100%;
    height: 100%;
    position: relative;
    overflow: hidden;
}

/* Bags Theme Mockup */
.bags-mockup {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.bags-mockup .mockup-header {
    height: 20%;
    background: rgba(255, 255, 255, 0.1);
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 10px;
}

.bags-mockup .mockup-nav {
    display: flex;
    gap: 8px;
}

.bags-mockup .nav-item {
    width: 24px;
    height: 3px;
    background: rgba(255, 255, 255, 0.7);
    border-radius: 2px;
}

.bags-mockup .mockup-content {
    height: 80%;
    background: white;
    padding: 15px;
}

.bags-mockup .content-header h4 {
    font-size: 14px;
    margin: 0 0 2px 0;
    color: var(--text-dark);
}

.bags-mockup .content-header p {
    font-size: 10px;
    margin: 0;
    color: #6c757d;
}

.bags-mockup .product-grid {
    margin-top: 12px;
}

.bags-mockup .main-product {
    width: 100%;
    height: 60px;
    margin-bottom: 8px;
}

.bags-mockup .main-image {
    width: 100%;
    height: 100%;
    background: linear-gradient(45deg, #ff9a9e 0%, #fecfef 100%);
    border-radius: 4px;
}

.bags-mockup .product-row {
    display: flex;
    gap: 6px;
}

.bags-mockup .small {
    flex: 1;
    height: 24px;
    background: linear-gradient(45deg, #a18cd1 0%, #fbc2eb 100%);
    border-radius: 3px;
}

/* Flex Theme Mockup */
.flex-mockup {
    background: white;
}

.flex-header {
    height: 20%;
    background: var(--secondary-turquoise);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 8px 12px;
}

.flex-logo {
    color: white;
    font-weight: bold;
    font-size: 12px;
}

.flex-nav {
    display: flex;
    gap: 6px;
}

.flex-nav .nav-item {
    width: 18px;
    height: 2px;
    background: rgba(255, 255, 255, 0.8);
    border-radius: 1px;
}

.flex-content {
    height: 80%;
    padding: 12px;
}

.flex-hero {
    height: 60%;
    display: flex;
    gap: 8px;
    margin-bottom: 8px;
}

.flex-hero .hero-image {
    flex: 2;
    background: linear-gradient(135deg, var(--secondary-turquoise) 0%, #20c997 100%);
    border-radius: 4px;
    position: relative;
}

.flex-hero .hero-products {
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.flex-hero .hero-product {
    flex: 1;
    background: linear-gradient(45deg, #fd7e14 0%, #ffc107 100%);
    border-radius: 3px;
}

.flex-products {
    height: 30%;
    display: flex;
    gap: 8px;
}

.flex-product {
    flex: 1;
    background: linear-gradient(45deg, #6f42c1 0%, #e83e8c 100%);
    border-radius: 4px;
}

/* Chic Theme Mockup */
.chic-mockup {
    background: white;
}

.chic-header {
    height: 20%;
    background: #f8f9fa;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 8px 12px;
    border-bottom: 1px solid #dee2e6;
}

.chic-logo {
    color: var(--text-dark);
    font-weight: 600;
    font-size: 12px;
}

.chic-nav {
    display: flex;
    gap: 6px;
}

.chic-nav .nav-item {
    width: 18px;
    height: 2px;
    background: #6c757d;
    border-radius: 1px;
}

.chic-content {
    height: 80%;
    padding: 12px;
}

.chic-hero {
    height: 60%;
    display: flex;
    position: relative;
    margin-bottom: 8px;
    border-radius: 4px;
    overflow: hidden;
}

.chic-hero .main-hero {
    width: 100%;
    background: linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%);
    position: relative;
}

.chic-hero .hero-text {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    text-align: center;
    color: var(--text-dark);
}

.chic-hero h5 {
    font-size: 8px;
    margin: 0 0 4px 0;
    font-weight: 600;
}

.chic-hero .cta-btn {
    background: var(--text-dark);
    color: white;
    border: none;
    padding: 2px 6px;
    border-radius: 2px;
    font-size: 6px;
    font-weight: 500;
}

.chic-products {
    height: 30%;
    display: flex;
    gap: 4px;
}

.chic-product {
    flex: 1;
    background: linear-gradient(45deg, #a8edea 0%, #fed6e3 100%);
    border-radius: 3px;
}

/* Theme Info */
.theme-info {
    margin-bottom: 16px;
}

.theme-name {
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--text-dark);
    margin-bottom: 4px;
}

.theme-badge {
    font-size: 0.75rem;
    color: var(--secondary-turquoise);
    background: rgba(23, 162, 184, 0.1);
    padding: 2px 8px;
    border-radius: 12px;
    font-weight: 500;
}

/* Theme Actions */
.theme-action {
    text-align: center;
}

.btn-theme-select {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    border: 2px solid var(--border-light);
    background: white;
    color: #6c757d;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: var(--transition);
}

.btn-theme-select.selected {
    background: var(--success-green);
    border-color: var(--success-green);
    color: white;
}

.btn-theme-apply {
    background: var(--accent-blue);
    border: none;
    color: white;
    padding: 8px 24px;
    border-radius: var(--border-radius);
    font-weight: 500;
    transition: var(--transition);
}

.btn-theme-apply:hover {
    background: #0056b3;
    transform: translateY(-1px);
}

/* Category Form */
.category-form-section {
    background: white;
    border-radius: var(--border-radius);
    padding: var(--spacing-base);
    box-shadow: var(--box-shadow);
    height: fit-content;
}

/* Flow Diagram */
.flow-diagram {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    background: #e8f5e8;
    border-radius: var(--border-radius);
    padding: var(--spacing-base);
    height: 400px;
}

.flow-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-bottom: 24px;
}

.flow-item:last-child {
    margin-bottom: 0;
}

.subcategory-flow {
    flex-direction: row;
    align-items: center;
}

.subcategory-flow .flow-box:first-child {
    margin-right: 16px;
}

.flow-box {
    padding: 16px 24px;
    border-radius: var(--border-radius);
    font-weight: 600;
    font-size: 1rem;
    text-align: center;
    min-width: 140px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.flow-box.primary {
    background: var(--accent-blue);
    color: white;
}

.flow-box.secondary {
    background: #ffc107;
    color: var(--text-dark);
}

.flow-box.accent {
    background: #a78bfa;
    color: white;
}

.flow-arrow {
    margin: 12px 0;
    font-size: 1.5rem;
    color: #6c757d;
}

.flow-arrow.horizontal {
    margin: 0 12px;
}

/* Product Form */
.product-form-wrapper {
    background: white;
    border-radius: var(--border-radius);
    box-shadow: var(--box-shadow);
    max-height: 80vh;
    overflow: hidden;
    display: flex;
    flex-direction: column;
}

.product-form-container {
    padding: var(--spacing-base);
    overflow-y: auto;
    flex: 1;
}

.product-form-container::-webkit-scrollbar {
    width: 6px;
}

.product-form-container::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 3px;
}

.product-form-container::-webkit-scrollbar-thumb {
    background: #c1c1c1;
    border-radius: 3px;
}

.product-form-container::-webkit-scrollbar-thumb:hover {
    background: #a8a8a8;
}

.form-section {
    border-bottom: 1px solid var(--border-light);
    padding-bottom: 24px;
}

.form-section:last-child {
    border-bottom: none;
    padding-bottom: 0;
}

/* Form Controls */
.form-label {
    font-weight: 500;
    color: var(--text-dark);
    margin-bottom: 8px;
}

.form-control {
    border: 1px solid var(--border-light);
    border-radius: var(--border-radius);
    padding: 12px 16px;
    font-size: 1rem;
    transition: var(--transition);
}

.form-control:focus {
    border-color: var(--accent-blue);
    box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.25);
}

.form-control::placeholder {
    color: #6c757d;
}

/* Textarea Toolbar */
.textarea-toolbar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 8px 12px;
    border: 1px solid var(--border-light);
    border-top: none;
    border-radius: 0 0 var(--border-radius) var(--border-radius);
    background: #f8f9fa;
}

.toolbar-icons {
    display: flex;
    gap: 12px;
}

.toolbar-icons i {
    color: #6c757d;
    cursor: pointer;
    transition: var(--transition);
}

.toolbar-icons i:hover {
    color: var(--accent-blue);
}

.char-count {
    font-size: 0.875rem;
    color: #6c757d;
}

/* Image Upload */
.image-upload-area {
    border: 2px dashed var(--border-light);
    border-radius: var(--border-radius);
    padding: 40px;
    text-align: center;
    transition: var(--transition);
    cursor: pointer;
}

.image-upload-area:hover {
    border-color: var(--accent-blue);
    background: rgba(0, 123, 255, 0.02);
}

.upload-placeholder {
    color: #6c757d;
    font-size: 2rem;
}

/* Checkboxes */
.form-check-input {
    margin-top: 4px;
}

.form-check-input:checked {
    background-color: var(--accent-blue);
    border-color: var(--accent-blue);
}

/* Product Preview */
.product-preview-section {
    position: sticky;
    top: var(--spacing-base);
}

.product-preview-card {
    background: white;
    border-radius: var(--border-radius);
    box-shadow: var(--box-shadow);
    overflow: hidden;
    max-width: 350px;
    margin: 0 auto;
}

.product-image-preview {
    aspect-ratio: 4/3;
    background: #ffedf0;
    position: relative;
    overflow: hidden;
}

.preview-placeholder {
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #ff9a9e 0%, #fecfef 50%, #fecfef 100%);
    position: relative;
}

.preview-placeholder::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 80px;
    height: 120px;
    background: #ff6b35;
    border-radius: 8px;
    opacity: 0.8;
}

.product-details-preview {
    padding: 20px;
}

.product-title-preview {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--text-dark);
    margin-bottom: 12px;
}

.product-description-preview {
    margin-bottom: 16px;
}

.product-description-preview p {
    font-size: 0.875rem;
    color: #6c757d;
    line-height: 1.5;
    margin: 0;
}

.product-price-preview {
    display: flex;
    align-items: center;
    gap: 8px;
}

.original-price {
    font-size: 1rem;
    color: #6c757d;
    text-decoration: line-through;
}

.discounted-price {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--text-dark);
}

/* Navigation */
.step-navigation {
    margin-top: calc(var(--spacing-base) * 2);
    padding: var(--spacing-base) 0;
    border-top: 1px solid var(--border-light);
    background: white;
}

.nav-buttons {
    display: flex;
    justify-content: space-between;
    align-items: center;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 var(--spacing-base);
}

.btn {
    border-radius: var(--border-radius);
    font-weight: 500;
    padding: 12px 24px;
    transition: var(--transition);
    border: none;
    font-size: 1rem;
}

.btn-primary {
    background: var(--accent-blue);
    color: white;
}

.btn-primary:hover {
    background: #0056b3;
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(0, 123, 255, 0.3);
}

.btn-outline-secondary {
    border: 1px solid #6c757d;
    color: #6c757d;
    background: transparent;
}

.btn-outline-secondary:hover {
    background: #6c757d;
    color: white;
}

/* Responsive Design */
@media (max-width: 991.98px) {
    .wizard-container {
        padding: 16px 0;
    }

    .step-title {
        font-size: 1.5rem;
    }

    .progress-bar-container {
        padding: 0 16px;
    }

    .step-number {
        width: 40px;
        height: 40px;
        font-size: 1rem;
    }

    .step-label {
        font-size: 0.75rem;
    }

    .progress-line {
        top: -26px;
        margin: 0 8px;
    }

    .theme-card {
        margin-bottom: 20px;
    }

    .flow-diagram {
        margin-top: 24px;
        height: auto;
        padding: 24px;
    }

    .subcategory-flow {
        flex-direction: column;
    }

    .subcategory-flow .flow-box:first-child {
        margin-right: 0;
        margin-bottom: 16px;
    }

    .flow-arrow.horizontal {
        margin: 12px 0;
        transform: rotate(90deg);
    }

    .product-form-wrapper {
        max-height: none;
        margin-bottom: 24px;
    }

    .product-preview-section {
        position: static;
    }
}

@media (max-width: 767.98px) {
    :root {
        --spacing-base: 16px;
    }

    .step-title {
        font-size: 1.25rem;
    }

    .section-title {
        font-size: 1.25rem;
    }

    .theme-selection-section,
    .category-form-section,
    .product-form-container {
        padding: 20px;
    }

    .flow-box {
        min-width: 120px;
        padding: 12px 16px;
        font-size: 0.875rem;
    }

    .nav-buttons {
        padding: 0 16px;
    }

    .progress-step {
        flex-direction: column;
    }

    .step-number {
        width: 36px;
        height: 36px;
        font-size: 0.875rem;
    }

    .step-label {
        font-size: 0.7rem;
    }

    .progress-line {
        top: -24px;
    }
}

@media (max-width: 575.98px) {
    .progress-bar-container {
        flex-wrap: wrap;
        gap: 16px;
    }

    .progress-line {
        display: none;
    }

    .theme-card {
        text-align: center;
    }

    .product-price-preview {
        flex-direction: column;
        align-items: flex-start;
        gap: 4px;
    }
}

/* Animation Classes */
.fade-in {
    animation: fadeIn 0.5s ease-out;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.slide-up {
    animation: slideUp 0.3s ease-out;
}

@keyframes slideUp {
    from {
        transform: translateY(10px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

js/wizard.js.content:
$(document).ready(function() {
    // Initialize wizard state
    let currentStep = 1;
    let wizardData = {
        theme: 'bags',
        productType: '',
        category: '',
        subcategory: '',
        productName: '',
        productDescription: '',
        skuCode: '',
        hasSku: true,
        hasHsn: false,
        priceInclusiveGst: true,
        netPrice: '',
        listPrice: '',
        discountPercentage: '',
        gstRate: '',
        shippingCharges: '',
        stockLevel: ''
    };

    // Initialize first step
    showStep(1);

    // Progress bar click handlers
    $('.progress-step').on('click', function() {
        const targetStep = parseInt($(this).data('step'));
        if (targetStep <= currentStep || validateCurrentStep()) {
            goToStep(targetStep);
        }
    });

    // Navigation button handlers
    $('.btn-next').on('click', function() {
        const nextStep = parseInt($(this).data('next'));
        if (validateCurrentStep()) {
            if (nextStep) {
                goToStep(nextStep);
            } else {
                // Final step completion
                handleWizardCompletion();
            }
        }
    });

    $('.btn-back').on('click', function() {
        const prevStep = parseInt($(this).data('prev'));
        if (prevStep) {
            goToStep(prevStep);
        }
    });

    // Theme selection handlers
    $('.theme-card').on('click', function() {
        const theme = $(this).data('theme');
        selectTheme(theme);
    });

    $('.btn-theme-apply').on('click', function(e) {
        e.stopPropagation();
        const theme = $(this).closest('.theme-card').data('theme');
        selectTheme(theme);
    });

    // Form input handlers
    $('#product-type').on('input', function() {
        wizardData.productType = $(this).val();
        updateFlowDiagram();
    });

    $('#category').on('input', function() {
        wizardData.category = $(this).val();
        updateFlowDiagram();
    });

    $('#subcategory').on('input', function() {
        wizardData.subcategory = $(this).val();
        updateFlowDiagram();
    });

    // Product form handlers
    $('#product-name').on('input', function() {
        wizardData.productName = $(this).val();
        updateProductPreview();
    });

    $('#product-description').on('input', function() {
        wizardData.productDescription = $(this).val();
        updateProductPreview();
        updateCharCount();
    });

    $('#sku-code').on('input', function() {
        wizardData.skuCode = $(this).val();
    });

    $('#has-sku').on('change', function() {
        wizardData.hasSku = $(this).prop('checked');
        toggleSkuInput();
    });

    $('#has-hsn').on('change', function() {
        wizardData.hasHsn = $(this).prop('checked');
    });

    $('#price-inclusive-gst').on('change', function() {
        wizardData.priceInclusiveGst = $(this).prop('checked');
    });

    // Pricing field handlers
    $('#net-price, #list-price').on('input', function() {
        const field = $(this).attr('id').replace('-', '');
        wizardData[field] = $(this).val();
        updateProductPreview();
        calculateDiscount();
    });

    $('#discount-percentage').on('input', function() {
        wizardData.discountPercentage = $(this).val();
    });

    $('#gst-rate').on('input', function() {
        wizardData.gstRate = $(this).val();
    });

    $('#shipping-charges').on('input', function() {
        wizardData.shippingCharges = $(this).val();
    });

    $('#stock-level').on('input', function() {
        wizardData.stockLevel = $(this).val();
    });

    // Image upload handler
    $('.image-upload-area').on('click', function() {
        // Simulate file upload dialog
        showToast('Image upload functionality would be implemented with file input', 'info');
    });

    // Toolbar icon handlers
    $('.toolbar-icons i').on('click', function() {
        const action = $(this).attr('class').split(' ')[1].split('-')[1];
        showToast(`${action.charAt(0).toUpperCase() + action.slice(1)} formatting applied`, 'info');
    });

    // Functions
    function goToStep(stepNumber) {
        if (stepNumber < 1 || stepNumber > 3) return;

        // Hide current step
        $('.step-content.active').removeClass('active').fadeOut(300);
        $('.progress-step.active').removeClass('active');

        // Update progress
        currentStep = stepNumber;

        // Show new step
        setTimeout(() => {
            $(`#step-${stepNumber}`).addClass('active').fadeIn(300);
            $(`.progress-step[data-step="${stepNumber}"]`).addClass('active');

            // Mark previous steps as completed
            for (let i = 1; i < stepNumber; i++) {
                $(`.progress-step[data-step="${i}"]`).addClass('completed');
            }

            // Remove completed class from future steps
            for (let i = stepNumber + 1; i <= 3; i++) {
                $(`.progress-step[data-step="${i}"]`).removeClass('completed');
            }
        }, 300);
    }

    function showStep(stepNumber) {
        currentStep = stepNumber;
        $('.step-content').removeClass('active');
        $('.progress-step').removeClass('active completed');

        $(`#step-${stepNumber}`).addClass('active');
        $(`.progress-step[data-step="${stepNumber}"]`).addClass('active');
    }

    function validateCurrentStep() {
        switch (currentStep) {
            case 1:
                if (!wizardData.theme) {
                    showToast('Please select a theme', 'error');
                    return false;
                }
                break;
            case 2:
                if (!wizardData.productType.trim()) {
                    showToast('Please enter a product type', 'error');
                    $('#product-type').focus();
                    return false;
                }
                break;
            case 3:
                if (!wizardData.productName.trim()) {
                    showToast('Please enter a product name', 'error');
                    $('#product-name').focus();
                    return false;
                }
                if (!wizardData.productDescription.trim()) {
                    showToast('Please enter a product description', 'error');
                    $('#product-description').focus();
                    return false;
                }
                break;
        }
        return true;
    }

    function selectTheme(theme) {
        wizardData.theme = theme;

        // Update UI
        $('.theme-card').removeClass('selected');
        $('.btn-theme-select').removeClass('selected');
        $('.btn-theme-apply').text('Apply');

        const selectedCard = $(`.theme-card[data-theme="${theme}"]`);
        selectedCard.addClass('selected');
        selectedCard.find('.btn-theme-select').addClass('selected');
        selectedCard.find('.btn-theme-apply').text('Selected');

        showToast(`${theme.charAt(0).toUpperCase() + theme.slice(1)} theme selected`, 'success');
    }

    function updateFlowDiagram() {
        const productTypeBox = $('.flow-box.primary');
        const categoryBox = $('.flow-box.secondary').first();
        const subcategoryBox = $('.flow-box.secondary').last();

        productTypeBox.text(wizardData.productType || 'Product type');
        categoryBox.text(wizardData.category || 'Category');
        subcategoryBox.text(wizardData.subcategory || 'Sub-Category');
    }

    function updateProductPreview() {
        const titlePreview = $('.product-title-preview');
        const descriptionPreview = $('.product-description-preview p');
        const originalPrice = $('.original-price');
        const discountedPrice = $('.discounted-price');

        titlePreview.text(wizardData.productName || 'Product title');
        descriptionPreview.text(wizardData.productDescription || 'Esse minim eiusmod amet et incididunt magna consectetur laborum. Ipsum est ullam do exercitation nostrud nostrud ex. Consectetur Lorem nostrud sint adipiscing amet ut cupidatat Lorem adipiscing veniam officia duis exercaetur Lorem labore. Non eu non');

        if (wizardData.listPrice) {
            originalPrice.text(`R$ ${wizardData.listPrice}`);
        }

        if (wizardData.netPrice) {
            discountedPrice.text(`R$ ${wizardData.netPrice}`);
        }
    }

    function updateCharCount() {
        const description = $('#product-description').val();
        $('.char-count').text(description.length);
    }

    function calculateDiscount() {
        const listPrice = parseFloat(wizardData.listPrice) || 0;
        const netPrice = parseFloat(wizardData.netPrice) || 0;

        if (listPrice > 0 && netPrice > 0) {
            const discount = Math.round(((listPrice - netPrice) / listPrice) * 100);
            $('#discount-percentage').val(discount);
            wizardData.discountPercentage = discount.toString();
        }
    }

    function toggleSkuInput() {
        const skuInput = $('#sku-code');
        if (wizardData.hasSku) {
            skuInput.prop('disabled', false).removeClass('disabled');
        } else {
            skuInput.prop('disabled', true).addClass('disabled');
        }
    }

    function handleWizardCompletion() {
        showToast('Shop setup completed successfully!', 'success');
        console.log('Wizard Data:', wizardData);

        // Here you would typically send the data to your backend
        // For now, we'll just show a completion message
        setTimeout(() => {
            alert('Congratulations! Your shop has been set up successfully.');
        }, 1000);
    }

    function showToast(message, type = 'info') {
        // Remove existing toasts
        $('.toast-notification').remove();

        const toastClass = {
            success: 'alert-success',
            error: 'alert-danger',
            info: 'alert-info',
            warning: 'alert-warning'
        }[type] || 'alert-info';

        const toast = $(`
            <div class="toast-notification alert ${toastClass} alert-dismissible fade show position-fixed" 
                 style="top: 20px; right: 20px; z-index: 9999; min-width: 300px;">
                <span>${message}</span>
                <button type="button" class="btn-close" data-bs-dismiss="alert"></button>
            </div>
        `);

        $('body').append(toast);

        // Auto-dismiss after 3 seconds
        setTimeout(() => {
            toast.fadeOut(300, function() {
                $(this).remove();
            });
        }, 3000);
    }

    // Initialize character count
    updateCharCount();

    // Initialize SKU input state
    toggleSkuInput();

    // Add smooth scrolling for step 3
    $('#step-3').on('shown', function() {
        const formContainer = $('.product-form-container');
        if (formContainer.length) {
            // Add custom scrollbar styling
            formContainer.addClass('custom-scrollbar');
        }
    });

    // Handle form submission prevention
    $('form').on('submit', function(e) {
        e.preventDefault();
    });

    // Add loading states for better UX
    $('.btn').on('click', function() {
        const btn = $(this);
        const originalText = btn.text();

        btn.prop('disabled', true).html('<i class="fas fa-spinner fa-spin me-2"></i>Loading...');

        setTimeout(() => {
            btn.prop('disabled', false).text(originalText);
        }, 500);
    });

    // Add hover effects for interactive elements
    $('.theme-card, .flow-box, .form-control').hover(
        function() {
            $(this).addClass('hover-effect');
        },
        function() {
            $(this).removeClass('hover-effect');
        }
    );

    // Initialize wizard with fade-in animation
    $('.wizard-container').addClass('fade-in');

    // Add responsive behavior for mobile
    function handleResponsive() {
        const isMobile = window.innerWidth < 768;

        if (isMobile) {
            $('.product-form-wrapper').css('max-height', 'none');
            $('.product-preview-section').css('position', 'static');
        } else {
            $('.product-form-wrapper').css('max-height', '80vh');
            $('.product-preview-section').css('position', 'sticky');
        }
    }

    // Handle window resize
    $(window).on('resize', handleResponsive);
    handleResponsive(); // Initial call

    // Add focus management for accessibility
    $('.btn-next, .btn-back').on('click', function() {
        setTimeout(() => {
            $('.step-content.active').find('input, textarea, select').first().focus();
        }, 300);
    });

    // Add keyboard navigation
    $(document).on('keydown', function(e) {
        if (e.ctrlKey || e.metaKey) {
            switch (e.which) {
                case 37: // Left arrow
                    if (currentStep > 1) {
                        e.preventDefault();
                        goToStep(currentStep - 1);
                    }
                    break;
                case 39: // Right arrow
                    if (currentStep < 3 && validateCurrentStep()) {
                        e.preventDefault();
                        goToStep(currentStep + 1);
                    }
                    break;
            }
        }
    });
});
